CREATE OR REPLACE FUNCTION rxt_obter_desc_equipamento (nr_seq_equip_p bigint) RETURNS varchar AS $body$
DECLARE


ds_equipamento_w	varchar(80);


BEGIN
if (nr_seq_equip_p IS NOT NULL AND nr_seq_equip_p::text <> '') then

	select	max(ds_equipamento)
	into STRICT	ds_equipamento_w
	from	rxt_equipamento
	where	nr_sequencia = nr_seq_equip_p;

end if;

return ds_equipamento_w;

end;
