SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION rxt_obter_dias_fase_pend (nr_seq_fase_p bigint) RETURNS bigint AS $body$
DECLARE


qt_retorno_w 		bigint;	
qt_duracao_trat_w	integer;
qt_agenda_exec_w	bigint;		


BEGIN

select	coalesce(max(qt_duracao_trat),0)
into STRICT	qt_duracao_trat_w
from	rxt_fase_tratamento
where	nr_sequencia = nr_seq_fase_p;

select 	count(*)
into STRICT	qt_agenda_exec_w
from	rxt_agenda
where	nr_Seq_fase = nr_seq_fase_p
and	ie_status_agenda = 'E';

if (qt_agenda_exec_w = 0) then

	select  count(*)
	into STRICT qt_agenda_exec_w
	from    rxt_agenda_fase a,
			rxt_agenda b
	where   a.nr_seq_fase = nr_seq_fase_p
	and     b.nr_sequencia = a.nr_seq_agenda_rxt
	and	    b.ie_status_agenda = 'E';

end if;

qt_retorno_w := qt_duracao_trat_w - qt_agenda_exec_w;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
