SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION rxt_obter_dose_total_aplic ( nr_seq_rxt_trat_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE



qt_dose_total_aplic_w bigint := 0;
qt_dose_restante_w bigint;
qt_dose_total_w bigint;


BEGIN

	--Dose total aplicada            
	select sum(c.qt_dose_aplic)
	into STRICT qt_dose_total_aplic_w
	from rxt_tratamento a,
	rxt_agenda b,
	rxt_campo_aplic c
	where c.nr_seq_agenda = b.nr_sequencia
	and b.nr_seq_tratamento = a.nr_sequencia
	and ie_evento_aplic = 'A'	
	and ie_evento_valido = 'S'
	and a.nr_sequencia = nr_seq_rxt_trat_p;

	--Dose restante
	select (a.qt_dose_total - qt_dose_total_aplic_w),
	a.qt_dose_total
	into STRICT qt_dose_restante_w,
	qt_dose_total_w
	from rxt_tratamento a
	where a.nr_sequencia = nr_seq_rxt_trat_p;

	if (ie_opcao_p = 'T') then
		if (qt_dose_total_aplic_w IS NOT NULL AND qt_dose_total_aplic_w::text <> '') then
			return qt_dose_total_aplic_w;
		else
			return 0;
		end if;
	else
		if (qt_dose_restante_w IS NOT NULL AND qt_dose_restante_w::text <> '') then
    			return qt_dose_restante_w;
		else
			return qt_dose_total_w;
		end if;
	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
