CREATE OR REPLACE FUNCTION rxt_obter_duracao_trat (nr_seq_equipamento_p bigint, nr_seq_tratamento_p bigint) RETURNS bigint AS $body$
DECLARE


qt_duracao_w	bigint;


BEGIN
if (nr_seq_equipamento_p IS NOT NULL AND nr_seq_equipamento_p::text <> '') and (nr_seq_tratamento_p IS NOT NULL AND nr_seq_tratamento_p::text <> '') then

	select	coalesce(months_between(trunc(max(dt_agenda),'month'),trunc(min(dt_agenda),'month')),0)+1
	into STRICT	qt_duracao_w		
	from	rxt_agenda
	where	nr_seq_equipamento	= nr_seq_equipamento_p
	and	nr_seq_tratamento	= nr_seq_tratamento_p
	and	ie_tipo_agenda		= 'T';

end if;

return qt_duracao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
