CREATE OR REPLACE FUNCTION rxt_obter_motivo_cancel_susp (nr_seq_tratamento_p bigint, ie_opcao bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_motivo_susp_w    bigint;
nr_seq_motivo_canc_w    bigint;
ds_motivo_w             varchar(200);


BEGIN

   select nr_seq_motivo_susp,
	  nr_seq_motivo_canc
   into STRICT	  nr_seq_motivo_susp_w,
	  nr_seq_motivo_canc_w
   from	  rxt_tratamento
   where  nr_sequencia = nr_seq_tratamento_p;

   if (ie_opcao = 1) then

	if (nr_seq_motivo_susp_w > 0) then
		ds_motivo_w := rxt_obter_motivo_canc(nr_seq_motivo_susp_w);
	end if;
   end if;

   if (ie_opcao = 2) then

	if (nr_seq_motivo_canc_w > 0) then
		ds_motivo_w := rxt_obter_motivo_canc(nr_seq_motivo_canc_w);
	end if;

   end if;

   return ds_motivo_w;



end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
