CREATE OR REPLACE FUNCTION rxt_obter_nome_fase_trat (nr_seq_fase_p bigint) RETURNS varchar AS $body$
DECLARE


nm_fase_w	varchar(40);


BEGIN
if (nr_seq_fase_p IS NOT NULL AND nr_seq_fase_p::text <> '') then

	select	max(nm_fase)
	into STRICT	nm_fase_w
	from	rxt_fase_tratamento
	where	nr_sequencia = nr_seq_fase_p;

end if;

return nm_fase_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
