CREATE OR REPLACE FUNCTION rxt_obter_observacao_medico (nr_seq_rxt_campo_p bigint, ie_tipo_trat_p text) RETURNS varchar AS $body$
DECLARE

    ds_observacao_medico_w	varchar(2000);

BEGIN

	if (ie_tipo_trat_p = 'T') then
		select 	ds_observacao_medico
		into STRICT	ds_observacao_medico_w
		from 	rxt_campo a
		where nr_sequencia = nr_seq_rxt_campo_p;
	elsif (ie_tipo_trat_p = 'B') then
		select 	ds_observacao_medico
		into STRICT	ds_observacao_medico_w
		from rxt_braq_campo_aplic_trat a
		where nr_sequencia = nr_seq_rxt_campo_p;
	end if;
    return	ds_observacao_medico_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
