CREATE OR REPLACE FUNCTION rxt_obter_pf_tratamento (nr_seq_tratamento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(10);


BEGIN

if (nr_seq_tratamento_p IS NOT NULL AND nr_seq_tratamento_p::text <> '') then

	select	max(b.cd_pessoa_fisica)
	into STRICT	cd_pessoa_fisica_w
	from	rxt_tumor b,
		rxt_tratamento a
	where	b.nr_sequencia = a.nr_seq_tumor
	and	a.nr_sequencia = nr_seq_tratamento_p;

end if;

return cd_pessoa_fisica_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
