CREATE OR REPLACE FUNCTION rxt_obter_prox_campo_aplic ( nr_seq_fase_p bigint, nr_seq_agenda_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_campo_w	bigint;


BEGIN
if (nr_seq_fase_p IS NOT NULL AND nr_seq_fase_p::text <> '') and (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then

	select	min(nr_sequencia)
	into STRICT	nr_seq_campo_w
	from	rxt_campo
	where	nr_seq_fase = nr_seq_fase_p
	and	rxt_obter_se_campo_aplic(nr_seq_agenda_p,nr_sequencia,'F') = 'N';

end if;

return nr_seq_campo_w;

end;
$body$
