CREATE OR REPLACE FUNCTION rxt_obter_se_agendamento (cd_pessoa_fisica_p text, nr_seq_tumor_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 	varchar(1);


BEGIN

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ds_retorno_w
from	rxt_agenda
where	cd_pessoa_fisica = cd_pessoa_fisica_p
and		ie_status_agenda not in ( 'E', 'C','SE');

if (ds_retorno_w = 'N') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	rxt_agenda a,
			rxt_tratamento b
	where	a.nr_seq_tratamento = b.nr_sequencia
	and		b.nr_seq_tumor 		= nr_seq_tumor_p
	and		coalesce(b.dt_suspensao::text, '') = ''
	and		coalesce(b.dt_cancelamento::text, '') = ''
	and		ie_status_agenda not in ( 'E', 'C','SE');

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
