CREATE OR REPLACE FUNCTION rxt_obter_se_agenda_dia ( nr_seq_equip_p bigint, dt_agenda_p timestamp, nr_seq_tratamento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_agenda_dia_w	varchar(1) := 'S';



BEGIN
if (nr_seq_equip_p IS NOT NULL AND nr_seq_equip_p::text <> '') and (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_agenda_dia_w
	from	rxt_agenda
	where	nr_seq_equipamento	= nr_seq_equip_p
	and	dt_agenda		between trunc(dt_agenda_p,'dd') and trunc(dt_agenda_p,'dd') + 86399/86400
	and	ie_tipo_agenda		= 'T'
	and (ie_status_agenda	= 'L'
	or (nr_seq_tratamento	= nr_seq_tratamento_p
	and	ie_status_agenda not in ('C','F')));

end if;

return ie_agenda_dia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
