CREATE OR REPLACE FUNCTION rxt_obter_se_agenda_pac_livre ( cd_pessoa_fisica_p text, dt_agenda_p timestamp, qt_duracao_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(1) := 'S';


BEGIN
select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
into STRICT	ds_retorno_w
from	agenda_paciente
where	cd_pessoa_fisica = cd_pessoa_fisica_p
and	dt_agenda between dt_agenda_p and dt_agenda_p + (qt_duracao_p/1440)
and	ie_status_agenda <> 'C';

if (ds_retorno_w = 'S') then
	select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
	into STRICT	ds_retorno_w
	from	agenda_quimio
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	dt_agenda between dt_agenda_p and dt_agenda_p + (qt_duracao_p/1440)
	and	ie_status_agenda <> 'C';
end if;

if (ds_retorno_w = 'S') then
	select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
	into STRICT	ds_retorno_w
	from	agenda_consulta
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	dt_agenda between dt_agenda_p and dt_agenda_p + (qt_duracao_p/1440)
	and	ie_status_agenda <> 'C';
end if;

if (ds_retorno_w = 'S') then
	select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
	into STRICT	ds_retorno_w
	from	rxt_agenda
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	dt_agenda between dt_agenda_p and dt_agenda_p + (qt_duracao_p/1440)
	and	ie_status_agenda <> 'C';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
