CREATE OR REPLACE FUNCTION rxt_obter_se_campo_aplic ( nr_seq_agenda_p bigint, nr_seq_campo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
F - Fase
B - Braquiterapia
*/
ie_aplicacao_w	varchar(1) := 'N';


BEGIN
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (nr_seq_campo_p IS NOT NULL AND nr_seq_campo_p::text <> '') then
	begin

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_aplicacao_w
	from	rxt_campo_aplic
	where	nr_seq_agenda	= nr_seq_agenda_p
	and	((nr_seq_campo	= nr_seq_campo_p and ie_opcao_p = 'F')
	or (nr_seq_campo_braq = nr_seq_campo_p and ie_opcao_p = 'B'))
	and	ie_evento_aplic	= 'A'
	and	ie_evento_valido	= 'S';

	end;
end if;

return ie_aplicacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
