CREATE OR REPLACE FUNCTION rxt_obter_se_fase_gerar_planej (nr_seq_tratamento_p bigint, nr_seq_fase_p bigint) RETURNS varchar AS $body$
DECLARE


ie_gerar_fase_w	varchar(1);


BEGIN
if (nr_seq_tratamento_p IS NOT NULL AND nr_seq_tratamento_p::text <> '') and (nr_seq_fase_p IS NOT NULL AND nr_seq_fase_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'C'  ELSE 'N' END
	into STRICT	ie_gerar_fase_w
	from	rxt_agenda
	where	nr_seq_tratamento 	= nr_seq_tratamento_p
	and	nr_seq_fase	= nr_seq_fase_p
	and	ie_status_agenda	not in ('L','C')
	and	ie_tipo_agenda = 'T';

end if;

return ie_gerar_fase_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
