CREATE OR REPLACE FUNCTION rxt_obter_se_inicio_trat ( nr_seq_tumor_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(01);
dt_inicio_trat_w	timestamp;

C01 CURSOR FOR
	SELECT	dt_inicio_trat
	from	rxt_tratamento
	where	nr_seq_tumor	= nr_seq_tumor_p;


BEGIN

ie_retorno_w	:= 'N';
if (nr_seq_tumor_p IS NOT NULL AND nr_seq_tumor_p::text <> '') then
	begin

	open C01;
	loop
	fetch C01 into
		dt_inicio_trat_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (dt_inicio_trat_w IS NOT NULL AND dt_inicio_trat_w::text <> '') then
			ie_retorno_w	:= 'S';
		else
			ie_retorno_w	:= 'N';
		end if;
		end;
	end loop;
	close C01;

	end;
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
