CREATE OR REPLACE FUNCTION rxt_obter_se_intercorrencia ( nr_seq_tratamento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_intercorrencia_w	varchar(1) := 'N';


BEGIN
if (nr_seq_tratamento_p IS NOT NULL AND nr_seq_tratamento_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_intercorrencia_w
	from	rxt_intercorrencia
	where	nr_seq_tratamento = nr_seq_tratamento_p;

end if;

return ie_intercorrencia_w;

end;
$body$
