SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION rxt_obter_se_lib_medico (nr_seq_fase_trat_p bigint, ie_tipo_trat_p text) RETURNS varchar AS $body$
DECLARE


qt_registros_w bigint;
qt_reg_lib_w bigint;
ie_lib_w  varchar(1) := 'N';

/*retorna se todos os aplicadores/fases estão liberados*/

BEGIN

if (ie_tipo_trat_p = 'T') then

	/*qt registros do tratamento teleterapia que só estão pendentes liberação médico*/

	select count(*)
	into STRICT qt_reg_lib_w
	from RXT_FASE_TRATAMENTO a
	where a.nr_seq_tratamento = nr_seq_fase_trat_p
	and  ((rxt_obter_se_fisico_lib(a.nr_sequencia, 'T') = 'N'
	and  not exists (SELECT 1
					from 	rxt_campo b
					where 	b.nr_seq_fase = a.nr_sequencia
					and (coalesce(b.dt_envio_fisico::text, '') = ''
					or 		coalesce(b.dt_lib_fisico::text, '') = '')
					and 	b.ie_situacao = 'A'))
	or not exists (select 1
					from 	rxt_campo b
					where 	b.nr_seq_fase = a.nr_sequencia));

	/*qt registros do tratamento de teleterapia*/

	select count(*)
	into STRICT qt_registros_w
	from RXT_FASE_TRATAMENTO
	where nr_seq_tratamento = nr_seq_fase_trat_p;

elsif (ie_tipo_trat_p = 'B') then
	/*qt registros do tratamento braquiterapia que só estão pendentes liberação médico*/

	select count(*)
	into STRICT qt_reg_lib_w
	from RXT_BRAQ_APLIC_TRAT a
	where a.nr_seq_tratamento = nr_seq_fase_trat_p
	and ((rxt_obter_se_fisico_lib(a.nr_sequencia, 'B') = 'N'
	and not exists (SELECT 	1
					from 	rxt_braq_campo_aplic_trat b
					where 	b.nr_seq_aplic_trat = a.nr_sequencia
					and (coalesce(b.dt_envio_fisico::text, '') = ''
					or 		coalesce(b.dt_lib_fisico::text, '') = '')
					and 	b.ie_situacao = 'A'))
	or not exists (select 1
					from 	rxt_braq_campo_aplic_trat b
					where 	b.nr_seq_aplic_trat = a.nr_sequencia));

	/*qt_registros do tratamento de braquiterapia*/

	select count(*)
	into STRICT qt_registros_w
	from RXT_BRAQ_APLIC_TRAT
	where nr_seq_tratamento = nr_seq_fase_trat_p;

end if;

	/*se todos os registros do tratamento estiverem pendentes liberação médico retornar S, senão retorna N*/

	if (qt_registros_w = qt_reg_lib_w) then
		ie_lib_w := 'S';
	else
		ie_lib_w := 'N';
	end if;

return ie_lib_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
