CREATE OR REPLACE FUNCTION rxt_obter_se_pac_check_film ( cd_paciente_p text, nr_seq_tratamento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_check_film_w	varchar(1) := 'N';


BEGIN
if (cd_paciente_p IS NOT NULL AND cd_paciente_p::text <> '') or (nr_seq_tratamento_p IS NOT NULL AND nr_seq_tratamento_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_check_film_w
	from	rxt_trat_check_film
	where (cd_paciente = cd_paciente_p) or (nr_seq_tratamento = nr_seq_tratamento_p)
	and	(dt_lib_tecnico IS NOT NULL AND dt_lib_tecnico::text <> '')
	and	coalesce(dt_lib_medico::text, '') = '';

end if;

return ie_check_film_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
