SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION rxt_obter_se_quimio ( dt_agenda_p timestamp, nr_seq_tratamento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_consiste_w	varchar(01);
dt_prevista_w	timestamp;


BEGIN

ie_consiste_w	:= 'N';
if (nr_seq_tratamento_p IS NOT NULL AND nr_seq_tratamento_p::text <> '') and (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') then
	begin

	select	a.dt_prevista
	into STRICT	dt_prevista_w
	from	paciente_setor b,
		paciente_atendimento a
	where	a.nr_seq_paciente = b.nr_seq_paciente
	and	b.cd_pessoa_fisica = substr(rxt_obter_pf_tratamento(nr_seq_tratamento_p,null),1,10)
	and	to_date(to_char(a.dt_prevista,'dd/mm/yyyy'),'dd/mm/yyyy') = trunc(dt_agenda_p);

	if (dt_prevista_w > dt_agenda_p) or (dt_prevista_w = dt_agenda_p) then
		ie_consiste_w	:= 'S';
	end if;

	end;
end if;

return	ie_consiste_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
