CREATE OR REPLACE FUNCTION rxt_obter_se_sobrepos_horario ( nr_seq_equip_p bigint, dt_agenda_p timestamp, nr_min_duracao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_sobreposicao_w	varchar(1) := 'N';

hr_param_w		timestamp;
nr_seq_horario_w	bigint;
hr_agenda_w		timestamp;


BEGIN

if (nr_seq_equip_p IS NOT NULL AND nr_seq_equip_p::text <> '') and (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') and (nr_min_duracao_p IS NOT NULL AND nr_min_duracao_p::text <> '') then

	/* obter horário parametro */

	hr_param_w := dt_agenda_p + nr_min_duracao_p / 1440;

	/* obter horário seguinte */

	select	min(dt_agenda)
	into STRICT	hr_agenda_w
	from	rxt_agenda
	where	nr_seq_equipamento = nr_seq_equip_p
	and	trunc(dt_agenda) = trunc(dt_agenda_p,'dd')
	and	dt_agenda > dt_agenda_p
	and	ie_status_agenda not in ('C','L');

	/* verificar sobreposição */

	if (hr_agenda_w IS NOT NULL AND hr_agenda_w::text <> '') then

		if (hr_param_w > hr_agenda_w) then

			ie_sobreposicao_w := 'S';
		else
			ie_sobreposicao_w := 'N';
		end if;
	else
		ie_sobreposicao_w := 'N';
	end if;
end if;

return ie_sobreposicao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
