CREATE OR REPLACE FUNCTION rxt_obter_se_todos_lib_fisico (nr_seq_fase_trat_p bigint, ie_tipo_trat_p text) RETURNS bigint AS $body$
DECLARE


qt_registros_w bigint;


BEGIN

if (ie_tipo_trat_p = 'T') then
	select count(*)
	into STRICT qt_registros_w
	from RXT_CAMPO a
	where nr_seq_fase = nr_seq_fase_trat_p
	and  ie_situacao = 'A'
	and  (dt_lib_fisico IS NOT NULL AND dt_lib_fisico::text <> '');

elsif (ie_tipo_trat_p = 'B') then
	select count(*)
	into STRICT qt_registros_w
	from rxt_braq_campo_aplic_trat a
	where nr_seq_aplic_trat = nr_seq_fase_trat_p
	and ie_situacao = 'A'
	and (dt_lib_fisico IS NOT NULL AND dt_lib_fisico::text <> '');

end if;


return qt_registros_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
