CREATE OR REPLACE FUNCTION rxt_obter_se_trat_planej (nr_seq_tratamento_p bigint, nr_seq_equipamento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_planej_w	varchar(1) := 'N';
ie_reagendar_hor_w	varchar(1);


BEGIN
if (nr_seq_tratamento_p IS NOT NULL AND nr_seq_tratamento_p::text <> '') and (nr_seq_equipamento_p IS NOT NULL AND nr_seq_equipamento_p::text <> '') then

	select	coalesce(max(ie_reagendar_hor),'N')
	into STRICT	ie_reagendar_hor_w
	from	rxt_parametro;


	if (ie_reagendar_hor_w = 'N') then

		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_planej_w
		from	rxt_agenda
		where	nr_seq_tratamento = nr_seq_tratamento_p
		and	nr_seq_equipamento = nr_seq_equipamento_p
		and	ie_status_agenda not in ('L','C')
		and	ie_tipo_agenda = 'T'
		and	not exists (	SELECT	1
						from	rxt_agenda
						where	nr_seq_tratamento = nr_seq_tratamento_p
						and	nr_seq_equipamento = nr_seq_equipamento_p
						and		ie_status_agenda in ('V','AP','E','A','T')
						and	ie_tipo_agenda = 'T'
					);
	else
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_planej_w
		from	rxt_agenda
		where	nr_seq_tratamento = nr_seq_tratamento_p
		and	nr_seq_equipamento = nr_seq_equipamento_p
		and	ie_status_agenda not in ('L','C')
		and	ie_tipo_agenda = 'T'
		and	not exists (	SELECT	1
						from	rxt_agenda
						where	nr_seq_tratamento = nr_seq_tratamento_p
						and	nr_seq_equipamento = nr_seq_equipamento_p
						and	ie_status_agenda in ('V','AP','A','T')
						and	ie_tipo_agenda = 'T'
					);

	end if;

end if;

return ie_planej_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
