CREATE OR REPLACE FUNCTION rxt_obter_status_pac_quimio ( dt_agenda_p timestamp, nr_seq_tratamento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_status_w		varchar(80);
nr_seq_atendimento_w	bigint;


BEGIN

if (nr_seq_tratamento_p IS NOT NULL AND nr_seq_tratamento_p::text <> '') and (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') then
	begin

	select	coalesce(max(a.nr_seq_atendimento),0)
	into STRICT	nr_seq_atendimento_w
	from	paciente_setor b,
		paciente_atendimento a
	where	a.nr_seq_paciente = b.nr_seq_paciente
	and	b.cd_pessoa_fisica = substr(rxt_obter_pf_tratamento(nr_seq_tratamento_p,null),1,10)
	and	to_date(to_char(a.dt_prevista,'dd/mm/yyyy'),'dd/mm/yyyy') = trunc(dt_agenda_p);

	if (nr_seq_atendimento_w > 0) then
		select	substr(Obter_status_Paciente_qt(a.nr_seq_atendimento,a.dt_inicio_adm,a.dt_fim_adm,a.nr_seq_local,a.ie_exige_liberacao,a.dt_chegada,'C'),1,80)
		into STRICT	ie_status_w
		from	paciente_atendimento a
		where	a.nr_seq_atendimento = nr_seq_atendimento_w;
	end if;

	end;
end if;

return	ie_status_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
