SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION rxt_obter_status_trat_ant ( nr_seq_agenda_p bigint, nr_seq_tratamento_p bigint, nr_seq_fase_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(15);
nr_seq_dia_w	bigint;
ds_parametro_w	varchar(1);



BEGIN

select	coalesce(max(ie_considera_tratamento_volume),'S')
into STRICT	ds_parametro_w
from 	rxt_parametro;

if (ds_parametro_w = 'S') then

	select	max(nr_seq_dia)
	into STRICT	nr_seq_dia_w
	from	rxt_agenda_fase
	where	nr_seq_agenda_rxt	= nr_seq_agenda_p;

	if (nr_seq_dia_w > 0) then

		select	max(a.ie_status_agenda)
		into STRICT	ds_retorno_w
		from	rxt_agenda a,
			rxt_agenda_fase b
		where	b.nr_seq_agenda_rxt	= a.nr_sequencia
		and	a.nr_seq_tratamento	= nr_seq_tratamento_p
		and	b.nr_seq_fase		= nr_seq_fase_p
		and	b.nr_seq_dia		= (nr_seq_dia_w - 1)
		and	a.ie_status_agenda <> 'C';

	end if;

else

	select	max(nr_seq_dia)
	into STRICT	nr_seq_dia_w
	from	rxt_Agenda
	where	nr_sequencia	= nr_seq_agenda_p;

	if (nr_seq_dia_w > 0) then

		select	max(ie_status_agenda)
		into STRICT	ds_retorno_w
		from	rxt_agenda
		where	nr_seq_tratamento	= nr_seq_tratamento_p
		and		nr_seq_fase		= nr_seq_fase_p
		and		nr_seq_dia		= (nr_seq_dia_w - 1)
		and		ie_status_agenda <> 'C';

	end if;

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION rxt_obter_status_trat_ant ( nr_seq_agenda_p bigint, nr_seq_tratamento_p bigint, nr_seq_fase_p bigint) FROM PUBLIC;

