CREATE OR REPLACE FUNCTION rxt_obter_tipo_trat_fase ( nr_seq_fase_p bigint) RETURNS varchar AS $body$
DECLARE

ds_tipo_w	varchar(5);

BEGIN

if (nr_seq_fase_p IS NOT NULL AND nr_seq_fase_p::text <> '') then
	select	max(d.ie_tipo)
	into STRICT	ds_tipo_w
	from	rxt_fase_tratamento a,
		rxt_tratamento b,
		rxt_tumor c,
		rxt_tipo d
	where	a.nr_sequencia = nr_seq_fase_p
	and	a.nr_seq_tratamento = b.nr_sequencia
	and	b.nr_seq_tumor = c.nr_sequencia
	and	c.nr_seq_tipo = d.nr_sequencia;
end if;

return	ds_tipo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
