SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION rxt_obter_valor_insercao ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
P - Protocolo
T - Tratamento
*/
ds_retorno_w	varchar(255);


BEGIN

if (ie_opcao_p = 'P') then

	select	max(CASE WHEN b.ds_tipo_resultado='N' THEN to_char(a.qt_resultado)  ELSE a.ds_resultado END )
	into STRICT	ds_retorno_w
	from	rxt_braq_campo_aplic_prot a,
		rxt_braq_campo b
	where	a.nr_seq_campo = b.nr_sequencia
	and	a.nr_sequencia = nr_sequencia_p;

elsif (ie_opcao_p = 'T') then

	select	max(CASE WHEN b.ds_tipo_resultado='N' THEN to_char(a.qt_resultado)  ELSE a.ds_resultado END )
	into STRICT	ds_retorno_w
	from	rxt_braq_campo_aplic_trat a,
		rxt_braq_campo b
	where	a.nr_seq_campo = b.nr_sequencia
	and	a.nr_sequencia = nr_sequencia_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
