SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION rxt_qt_plan_fisicos (nr_seq_fase_trat_p bigint, ie_origem_p text, ie_tipo_trat_p text) RETURNS varchar AS $body$
DECLARE


qt_registros	bigint := 0;

/*
f - origem físico
m - origem médico*/
BEGIN

	if (ie_origem_p = 'F') then
		if (ie_tipo_trat_p = 'T') then
			select 	count(*)
			into STRICT	qt_registros
			from 	rxt_planejamento_fisico a
			where 	coalesce(dt_liberacao::text, '') = ''
			and 	ie_situacao = 'A'
			and 	nr_seq_fase = nr_seq_fase_trat_p;

		elsif (ie_tipo_trat_p = 'B') then
			select 	count(*)
			into STRICT	qt_registros
			from 	rxt_plan_braqui_fisico a
			where 	coalesce(dt_liberacao::text, '') = ''
			and 	ie_situacao = 'A'
			and 	nr_seq_braq_aplic_trat = nr_seq_fase_trat_p;

		end if;
	elsif (ie_origem_p = 'M') then
		if (ie_tipo_trat_p = 'T') then
			select 	count(*)
			into STRICT	qt_registros
			from 	rxt_campo a
			where 	coalesce(dt_liberacao::text, '') = ''
			and 	ie_situacao = 'A'
			and 	nr_seq_fase = nr_seq_fase_trat_p;

		elsif (ie_tipo_trat_p = 'B') then
			select 	count(*)
			into STRICT	qt_registros
			from 	rxt_braq_campo_aplic_trat a
			where 	coalesce(dt_liberacao::text, '') = ''
			and 	ie_situacao = 'A'
			and 	nr_seq_aplic_trat = nr_seq_fase_trat_p;

		end if;
	end if;

    return	qt_registros;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
