CREATE OR REPLACE FUNCTION rxt_ret_hor_prox_agenda (nr_seq_tratamento_p bigint, nr_seq_equipamento_p bigint, dt_prevista_p timestamp, nm_usuario_p text) RETURNS timestamp AS $body$
DECLARE


dt_agendada_seguinte_w		timestamp;


BEGIN

	select	coalesce(max(dt_agenda),dt_prevista_p)
	into STRICT	dt_agendada_seguinte_w
	from	rxt_agenda_temp
	where	nm_usuario = nm_usuario_p
	and	trunc(dt_agenda) = trunc(dt_prevista_p);

return	dt_agendada_seguinte_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
