CREATE OR REPLACE FUNCTION rxt_tratamento_pend_agend_simu ( nr_seq_tratamento_p rxt_tratamento.nr_sequencia%TYPE ) RETURNS varchar AS $body$
DECLARE


nr_count_w smallint;


BEGIN

    SELECT COUNT(tratamento.nr_sequencia)
    INTO STRICT nr_count_w
    FROM
        rxt_tratamento tratamento,
        rxt_tumor tumor
    WHERE tratamento.nr_sequencia = nr_seq_tratamento_p
    AND tratamento.nr_seq_tumor = tumor.nr_sequencia
    AND rxt_obter_tipo_tratamento(tumor.nr_seq_tipo) = 'T' 
    AND rxt_tratamento_ag_autorizacao(tratamento.nr_sequencia) <> 'S'
    AND coalesce(tratamento.dt_liberacao::text, '') = ''
    AND coalesce(tratamento.dt_suspensao::text, '') = ''
    AND coalesce(tratamento.dt_cancelamento::text, '') = ''
    AND rxt_tratamento_finalizado(tratamento.nr_sequencia) <> 'S'
    AND (SELECT COUNT(*)
        FROM rxt_agenda
        WHERE cd_pessoa_fisica = tumor.cd_pessoa_fisica
        AND ie_status_agenda NOT IN ('B','C')
        AND ie_tipo_agenda = 'S'
        ) = 0;

    IF (nr_count_w > 0) THEN
        RETURN 'S';
    ELSE
        RETURN 'N';
    END IF;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
