CREATE OR REPLACE FUNCTION rxt_valida_se_planej_livre (nr_seq_tratamento_p bigint, nr_seq_equipamento_p bigint, qt_duracao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_reagendar_hor_w	varchar(1);
ds_retorno_w		varchar(2000) := '';
cd_pessoa_fisica_w	varchar(10);
ie_agenda_livre_w	varchar(1);
dt_agenda_w		timestamp;

c01 CURSOR FOR
SELECT	dt_agenda
from	rxt_agenda
where	nr_seq_equipamento	= nr_seq_equipamento_p
and	nr_seq_tratamento	= nr_seq_tratamento_p
and	ie_status_agenda <> 'C'
order by
	dt_agenda;

c02 CURSOR FOR
SELECT	dt_agenda
from	rxt_agenda
where	nr_seq_equipamento	= nr_seq_equipamento_p
and	nr_seq_tratamento		= nr_seq_tratamento_p
and	ie_status_agenda not in ('C','E')
order by
	dt_agenda;


BEGIN
if (nr_seq_tratamento_p IS NOT NULL AND nr_seq_tratamento_p::text <> '') and (nr_seq_equipamento_p IS NOT NULL AND nr_seq_equipamento_p::text <> '') and (qt_duracao_p IS NOT NULL AND qt_duracao_p::text <> '')  then

	select	max(a.cd_pessoa_fisica)
	into STRICT	cd_pessoa_fisica_w
	from	rxt_tumor a,
		rxt_tratamento b
	where	a.nr_sequencia = b.nr_seq_tumor
	and	b.nr_sequencia = nr_seq_tratamento_p;

	select	coalesce(max(ie_reagendar_hor),'N')
	into STRICT	ie_reagendar_hor_w
	from	rxt_parametro;

	if (ie_reagendar_hor_w = 'N') then

		open c01;
		loop
		fetch c01 into	dt_agenda_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			ie_agenda_livre_w := rxt_obter_se_agenda_pac_livre(cd_pessoa_fisica_w,dt_agenda_w,qt_duracao_p);
			if (ie_agenda_livre_w = 'N') then
				if (ds_retorno_w = '') or (coalesce(ds_retorno_w::text, '') = '') then
					ds_retorno_w := wheb_mensagem_pck.get_texto(799638) || ': '||chr(10);
				end if;
				ds_retorno_w := ds_retorno_w || to_char(dt_agenda_w,pkg_date_formaters.localize_mask('timestamp', pkg_date_formaters.getUserLanguageTag(wheb_usuario_pck.get_cd_estabelecimento, wheb_usuario_pck.get_nm_usuario)))||chr(10);
			end if;
			end;
		end loop;
		close c01;
	else
		open c02;
		loop
		fetch c02 into	dt_agenda_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
			begin
			ie_agenda_livre_w := rxt_obter_se_agenda_pac_livre(cd_pessoa_fisica_w,dt_agenda_w,qt_duracao_p);

			if (ie_agenda_livre_w = 'N') then
				if (ds_retorno_w = '') or (coalesce(ds_retorno_w::text, '') = '') then
					ds_retorno_w := wheb_mensagem_pck.get_texto(799638) || ': '||chr(10);
				end if;
				ds_retorno_w := ds_retorno_w || to_char(dt_agenda_w,pkg_date_formaters.localize_mask('timestamp', pkg_date_formaters.getUserLanguageTag(wheb_usuario_pck.get_cd_estabelecimento, wheb_usuario_pck.get_nm_usuario)))||chr(10);
			end if;
			end;
		end loop;
		close c02;
	end if;
end if;

if (ds_retorno_w = '') or (coalesce(ds_retorno_w::text, '') = '') then
	ds_retorno_w := 'S';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
