SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION rxt_verifica_vinculo_atend ( nr_sequencia_p bigint ) RETURNS varchar AS $body$
DECLARE


ie_vinculado_w			varchar(1) := 'S';
ie_vinculo_protocolo_w		varchar(1);
ie_vinculo_tratamento_w		varchar(1);
ie_vinculo_atend_w		varchar(1);
ie_trat_sem_protocolo_w	varchar(1);

BEGIN

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_vinculo_protocolo_w
from	rxt_agenda a,
	rxt_tratamento b,
	rxt_protocolo c,
	rxt_protocolo_proc_exec d
where	a.nr_seq_tratamento = b.nr_sequencia
and	b.nr_seq_protocolo = c.nr_sequencia
and	c.nr_sequencia = d.nr_seq_protocolo
and	a.nr_sequencia = nr_sequencia_p;

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_vinculo_tratamento_w
from	rxt_agenda a,
	rxt_tratamento b,
	rxt_protocolo c,
	rxt_tipo d,
	rxt_tipo_trat_proced e
where	a.nr_seq_tratamento = b.nr_sequencia
and	b.nr_seq_protocolo = c.nr_sequencia
and	c.nr_seq_tipo = d.nr_sequencia
and	d.nr_sequencia = e.nr_seq_tipo
and	a.nr_sequencia = nr_sequencia_p;

select CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_trat_sem_protocolo_w
from	rxt_agenda a,
		rxt_tratamento b,
		rxt_tumor c,
		rxt_tipo_trat_proced d
where	a.nr_seq_tratamento = b.nr_sequencia
and		b.nr_seq_tumor = c.nr_sequencia
and		c.nr_seq_tipo = d.nr_seq_tipo
and		coalesce(b.nr_seq_protocolo::text, '') = ''
and		a.nr_sequencia = nr_sequencia_p
and		coalesce(d.ie_situacao,'A') = 'A';

if (ie_trat_sem_protocolo_w = 'N') then
	select CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_trat_sem_protocolo_w
	from	rxt_agenda a,
			rxt_tratamento b
	where	a.nr_sequencia = nr_sequencia_p
	and		a.nr_seq_tratamento = b.nr_sequencia
	and		coalesce(b.nr_seq_protocolo::text, '') = '';

	if (ie_trat_sem_protocolo_w = 'S') then
		select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_trat_sem_protocolo_w
		from	RXT_PROCED_FATURAMENTO
		where	IE_TIPO_LANCAMENTO = 'R'
		and		coalesce(ie_situacao,'A') = 'A';
	end if;

end if;

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_vinculo_atend_w
from	rxt_agenda
where	nr_sequencia = nr_sequencia_p
and	(nr_atendimento IS NOT NULL AND nr_atendimento::text <> '');

if (((ie_vinculo_protocolo_w = 'S') or (ie_vinculo_tratamento_w = 'S') or (ie_trat_sem_protocolo_w = 'S')) and ie_vinculo_atend_w = 'N') then
	ie_vinculado_w := 'N';
end if;

return	ie_vinculado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION rxt_verifica_vinculo_atend ( nr_sequencia_p bigint ) FROM PUBLIC;

