CREATE OR REPLACE FUNCTION sac_obter_dados_analise_bo (nr_seq_boletim_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_seq_tipo_causa_w	bigint;
cd_setor_atendimento_w	integer;



BEGIN
Select	max(nr_seq_tipo_causa),
	max(cd_setor_atendimento)
into STRICT	nr_seq_tipo_causa_w,
	cd_setor_atendimento_w
from	sac_boletim_ocor_analise
where	nr_seq_boletim_ocorrencia = nr_seq_boletim_p;

if (ie_opcao_p = 'T') then
	begin
	select	ds_tipo_causa
	into STRICT	ds_retorno_w
	from	sac_tipo_causa
	where	nr_sequencia = nr_seq_tipo_causa_w;
	end;
elsif (ie_opcao_p = 'S') then
	begin
	select	ds_setor_atendimento
	into STRICT	ds_retorno_w
	from	setor_atendimento
	where	cd_setor_atendimento = cd_setor_atendimento_w;
	end;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
