CREATE OR REPLACE FUNCTION sac_obter_dados_bo ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_assunto_w		varchar(255);
ie_possui_analise_w	varchar(1);

/* ie_opcao_p
A - Assunto
PA - Possui an√°lise
*/
BEGIN
select	a.ds_assunto
into STRICT	ds_assunto_w
from	sac_boletim_ocorrencia a
where	a.nr_sequencia = nr_sequencia_p;

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_possui_analise_w
from	sac_boletim_ocor_analise
where	nr_seq_boletim_ocorrencia = nr_sequencia_p;

if (ie_opcao_p = 'A') then
	ds_retorno_w := ds_assunto_w;
elsif (ie_opcao_p = 'PA') then
	ds_retorno_w := ie_possui_analise_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
