CREATE OR REPLACE FUNCTION sac_obter_dados_resposta ( nr_seq_resposta_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN
if (nr_seq_resposta_p > 0) then
	begin
	if (ie_opcao_p = 'R') then
		begin
		select	ds_responsavel
		into STRICT	ds_retorno_w
		from	sac_responsavel
		where	nr_sequencia = (SELECT	nr_seq_responsavel
					from	sac_resp_bol_ocor
					where	nr_sequencia = nr_seq_resposta_p);
		end;
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
