CREATE OR REPLACE FUNCTION sac_obter_data_avaliacao ( nr_sequencia_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w			timestamp;
dt_resposta_w			timestamp;
qt_dias_avaliacao_sac_w		bigint;



BEGIN

select	coalesce(max(a.qt_dias_avaliacao_sac),0)
into STRICT	qt_dias_avaliacao_sac_w
from	sac_boletim_ocorrencia a
where	a.nr_sequencia = nr_sequencia_p
and	exists (SELECT	1
		from	sac_resp_bol_ocor x
		where	x.nr_seq_bo = a.nr_sequencia);

if (qt_dias_avaliacao_sac_w > 0) then
	begin

	select	coalesce(max(dt_resposta),clock_timestamp())
	into STRICT	dt_resposta_w
	from	sac_resp_bol_ocor
	where	nr_seq_bo = nr_sequencia_p;

	dt_retorno_w	:= trunc(dt_resposta_w) + qt_dias_avaliacao_sac_w;

	end;
end if;

return dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
