elsif (ie_status_p = 'P') then
	--'Respondida parcialmente'
	ds_retorno_w := substr(wheb_mensagem_pck.get_texto(305373),1,60);
elsif (ie_status_p = 'E') then
	--'Encerrada / Atendida'
	ds_retorno_w := substr(wheb_mensagem_pck.get_texto(305374),1,60);
elsif (ie_status_p = 'S') then
	--'Encerrada sem resposta'
	ds_retorno_w := substr(wheb_mensagem_pck.get_texto(305375),1,60);
elsif (ie_status_p = 'B') then
	--'Reaberta'
	ds_retorno_w := substr(wheb_mensagem_pck.get_texto(305376),1,60);
elsif (ie_status_p = 'N') then
	--'Encaminhada'
	ds_retorno_w := substr(wheb_mensagem_pck.get_texto(305377),1,60);
elsif (ie_status_p = 'G') then
	--'Entregue'
	ds_retorno_w := substr(wheb_mensagem_pck.get_texto(305378),1,60);
elsif (ie_status_p = 'C') then
	--'Encerrada com resposta ao cliente'
	ds_retorno_w := substr(wheb_mensagem_pck.get_texto(305379),1,60);
else
	--'NÃ£o informado'
	ds_retorno_w := substr(wheb_mensagem_pck.get_texto(305380),1,60);
end if;

return	ds_retorno_w;

end;
