CREATE OR REPLACE FUNCTION sac_obter_se_alt_pasta_bo_lib ( ie_pasta_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'N';
qt_registro_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	sac_regra_alt_lib_bo;

if (qt_registro_w > 0) then
	begin
	select	ie_permite
	into STRICT	ie_retorno_w
	from	sac_regra_alt_lib_bo
	where	ie_pasta = ie_pasta_p  LIMIT 1;
	exception
	when others then
		ie_retorno_w := 'N';
	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
