SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sac_obter_se_bo_novo_hist ( nr_seq_boletim_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'N';
dt_ultimo_acesso_w	timestamp;
ie_permite_lib_hist_w	varchar(255);


BEGIN

begin
select	'S'
into STRICT	ie_retorno_w
from	sac_resp_bol_ocor a
where	obter_dados_sac_resp(a.nr_seq_responsavel,nm_usuario_p,'R') = 'S'
and	a.nr_seq_bo = nr_seq_boletim_p  LIMIT 1;
exception
when others then
	ie_retorno_w := 'N';
end;

if (ie_retorno_w = 'S') then
	begin
	ie_permite_lib_hist_w := coalesce(obter_valor_param_usuario(2000,184,obter_perfil_ativo,nm_usuario_p,wheb_usuario_pck.get_cd_estabelecimento),'N');

	select	coalesce(max(dt_ultimo_acesso),to_date('21/01/2014', 'dd/mm/yyyy hh24:mi:ss'))
	into STRICT	dt_ultimo_acesso_w
	from	sac_log_acesso_bo
	where	nr_seq_boletim = nr_seq_boletim_p
	and	nm_usuario = nm_usuario_p;

	if (ie_permite_lib_hist_w = 'S') then
		begin
		select	'S'
		into STRICT	ie_retorno_w
		from	sac_hist_bol_ocor b,
			sac_resp_bol_ocor a
		where	a.nr_sequencia = b.nr_seq_resp_bo
		and	obter_dados_sac_resp(a.nr_seq_responsavel,nm_usuario_p,'R') = 'S'
		and	b.nm_usuario_lib <> nm_usuario_p
		and	b.dt_liberacao > dt_ultimo_acesso_w
		and	a.nr_seq_bo = nr_seq_boletim_p  LIMIT 1;
		exception
		when others then
			ie_retorno_w := 'N';
		end;
	else
		begin
		select	'S'
		into STRICT	ie_retorno_w
		from	sac_hist_bol_ocor b,
			sac_resp_bol_ocor a
		where	a.nr_sequencia = b.nr_seq_resp_bo
		and	obter_dados_sac_resp(a.nr_seq_responsavel,nm_usuario_p,'R') = 'S'
		and	b.nm_usuario_nrec <> nm_usuario_p
		and	b.dt_atualizacao_nrec > dt_ultimo_acesso_w
		and	a.nr_seq_bo = nr_seq_boletim_p  LIMIT 1;
		exception
		when others then
			ie_retorno_w := 'N';
		end;
	end if;
	end;
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
