CREATE OR REPLACE FUNCTION sac_obter_se_existe_setor_adic (nr_seq_responsavel_p bigint) RETURNS varchar AS $body$
DECLARE


qt_setor_w		double precision := 0;
qt_setor_adic_w double precision := 0;
ie_setor_adic_w	varchar(1) := '';


BEGIN

select	count(cd_setor_atendimento)
into STRICT	qt_setor_w
from	sac_responsavel
where	nr_sequencia = nr_seq_responsavel_p;


select	count(cd_setor_atendimento)
into STRICT	qt_setor_adic_w
from	sac_responsavel_setor_adic
where	nr_seq_responsavel = nr_seq_responsavel_p;

if	((coalesce(qt_setor_w,0) + coalesce(qt_setor_adic_w,0)) <> 0) then
	ie_setor_adic_w := 'S';
else
	ie_setor_adic_w := 'N';
end if;


return	ie_setor_adic_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
