CREATE OR REPLACE FUNCTION same_obter_cod_localizacao ( nr_seq_local_p bigint, ie_separador_p text default null) RETURNS varchar AS $body$
DECLARE

ds_codigo_localizacao_w		varchar(255) := '';
cd_armario_w			varchar(5) := '';
cd_prateleira_w			varchar(5) := '';
cd_posicao_w			varchar(5) := '';

BEGIN
if (nr_seq_local_p IS NOT NULL AND nr_seq_local_p::text <> '')  and (coalesce(ie_separador_p::text, '') = '') then

	select	cd_armario||cd_prateleira||cd_posicao
	into STRICT	ds_codigo_localizacao_w
	from	same_local
	where	nr_sequencia = nr_seq_local_p;

else if (nr_seq_local_p IS NOT NULL AND nr_seq_local_p::text <> '')  and (ie_separador_p IS NOT NULL AND ie_separador_p::text <> '') then

	select	cd_armario,
		cd_prateleira,
		cd_posicao
	into STRICT	cd_armario_w,
		cd_prateleira_w,
		cd_posicao_w
	from	same_local
	where	nr_sequencia = nr_seq_local_p;

	if (cd_armario_w IS NOT NULL AND cd_armario_w::text <> '') or (cd_prateleira_w IS NOT NULL AND cd_prateleira_w::text <> '') or (cd_posicao_w IS NOT NULL AND cd_posicao_w::text <> '') then
		select cd_armario_w||ie_separador_p||CASE WHEN coalesce(cd_prateleira_w::text, '') = '' THEN ' '  ELSE cd_prateleira_w END ||ie_separador_p||CASE WHEN coalesce(cd_posicao_w::text, '') = '' THEN ' '  ELSE cd_posicao_w END
		into STRICT	ds_codigo_localizacao_w
		;
	end if;

     end if;

end if;

return	ds_codigo_localizacao_w;
end;
$body$
