CREATE OR REPLACE FUNCTION same_obter_cor_atendimento ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

					 
cd_estabelecimento_w	integer;
ie_tipo_atendimento_w	smallint;
nr_seq_cor_w		bigint	:= 0;
ds_cor_fundo_w		varchar(255);
ds_cor_fonte_w    	varchar(255);
ds_cor_selecao_w	varchar(255);
	

BEGIN 
 
select	max(cd_estabelecimento), 
	max(ie_tipo_atendimento) 
into STRICT	cd_estabelecimento_w, 
	ie_tipo_atendimento_w 
from	atendimento_paciente 
where	nr_atendimento	=	nr_atendimento_p;
 
if (ie_tipo_atendimento_w = 1) then 
	nr_seq_cor_w	:= 197;
elsif (ie_tipo_atendimento_w = 3) then 
	nr_seq_cor_w	:= 198;
elsif (ie_tipo_atendimento_w = 7) then 
	nr_seq_cor_w	:= 199;
elsif (ie_tipo_atendimento_w = 8) then 
	nr_seq_cor_w	:= 200;
end if;
 
SELECT * FROM tasy_obter_cor(nr_seq_cor_w, obter_perfil_ativo, cd_estabelecimento_w, ds_cor_fundo_w, ds_cor_fonte_w, ds_cor_selecao_w) INTO STRICT ds_cor_fundo_w, ds_cor_fonte_w, ds_cor_selecao_w;
 
return	ds_cor_fundo_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
