CREATE OR REPLACE FUNCTION same_obter_estab_solic_armz ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(50);
cd_estabelecimento_w	smallint;
nr_seq_local_w		bigint;
nm_fantasia_estab_w	varchar(50);

BEGIN
select	max(nr_seq_local)
into STRICT	nr_seq_local_w
from	same_prontuario
where	((nr_atendimento = nr_atendimento_p) or (coalesce(nr_atendimento::text, '') = '') or (coalesce(nr_atendimento_p::text, '') = ''))
and	cd_pessoa_fisica = cd_pessoa_fisica_p;

select	max(cd_estabelecimento)
into STRICT	cd_estabelecimento_w
from	same_local
where	nr_sequencia = nr_seq_local_w;

select	max(nm_fantasia_estab)
into STRICT	nm_fantasia_estab_w
from	estabelecimento
where	cd_estabelecimento = cd_estabelecimento_w;

if (ie_opcao_p = 'C') then
	ds_retorno_w := cd_estabelecimento_w;
elsif (ie_opcao_p = 'D') then
	ds_retorno_w := nm_fantasia_estab_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
