CREATE OR REPLACE FUNCTION same_obter_se_caixa_lib (nr_seq_solic_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
nr_atendimento_w	bigint;
cd_pessoa_fisica_w	varchar(10);

/*
* Par창metro [26] - C ou O
1 - Caixa Liberada
2 - Caixa n찾o liberada

* Par창metro [26] - N
3 - Caixa Liberada
4 - Caixa n찾o liberada
*/
BEGIN

if (nr_seq_solic_p IS NOT NULL AND nr_seq_solic_p::text <> '') and (ie_opcao_p < 3) then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from  	SAME_SOLIC_PRONT_ENVELOPE a,
			same_prontuario b,
			same_caixa c
	where	a.nr_seq_prontuario = b.nr_sequencia
	and		b.nr_seq_caixa = c.nr_sequencia
	and		((coalesce(ie_opcao_p,1) = 1 and (c.dt_liberacao IS NOT NULL AND c.dt_liberacao::text <> '')) or (coalesce(ie_opcao_p,1) = 2 and coalesce(c.dt_liberacao::text, '') = ''))
	and		a.nr_seq_solic = nr_seq_solic_p;

elsif (nr_seq_solic_p IS NOT NULL AND nr_seq_solic_p::text <> '') and (ie_opcao_p > 2) then

        select 	max(nr_atendimento),
				max(cd_pessoa_fisica)
		into STRICT	nr_atendimento_w,
				cd_pessoa_fisica_w
		from	same_solic_pront
		where	nr_sequencia = nr_seq_solic_p;

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from  	same_prontuario b,
			same_caixa c
	where	b.nr_seq_caixa = c.nr_sequencia
	and		((b.nr_atendimento = coalesce(nr_atendimento_w,0)) or (coalesce(nr_atendimento_w,0) = 0))
	and		((b.cd_pessoa_fisica = coalesce(cd_pessoa_Fisica_w,'0')) or (coalesce(cd_pessoa_fisica_w,'0') = '0'))
	and		((coalesce(ie_opcao_p,3) = 3 and (c.dt_liberacao IS NOT NULL AND c.dt_liberacao::text <> '')) or (coalesce(ie_opcao_p,3) = 4 and coalesce(c.dt_liberacao::text, '') = ''));

end if;

return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
