CREATE OR REPLACE FUNCTION same_obter_se_exibe_agenda (dt_inicio_p timestamp, dt_fim_p timestamp, cd_agenda_p bigint) RETURNS varchar AS $body$
DECLARE

ie_exibe_agenda_w varchar(1);
ie_parametro_w	  varchar(1);


BEGIN

ie_parametro_w := obter_param_usuario(941, 181, Obter_perfil_Ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_parametro_w);

if (ie_parametro_w = 'S') then

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_exibe_agenda_w
from	agenda_consulta a
where	a.cd_agenda = cd_agenda_p
and	a.dt_agenda between inicio_dia(dt_inicio_p) and fim_dia(dt_fim_p)
and	(a.cd_pessoa_fisica IS NOT NULL AND a.cd_pessoa_fisica::text <> '')
and	a.ie_status_agenda <> 'C'
and	coalesce(obter_prontuario_pf(wheb_usuario_pck.get_cd_estabelecimento,a.cd_pessoa_fisica),0) > 0;

end if;

if (ie_parametro_w = 'N') then
	ie_exibe_agenda_w := 'S';
end if;

return	ie_exibe_agenda_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
