CREATE OR REPLACE FUNCTION san_calc_volume_hemo_lavagem ( nr_seq_lavagem_p bigint, qt_peso_bolsa_p bigint) RETURNS bigint AS $body$
DECLARE


qt_densidade_w		double precision;
qt_volume_w		double precision;
nr_seq_derivado_w	bigint;


BEGIN

select	max(b.nr_seq_derivado)
into STRICT	nr_seq_derivado_w
from	san_producao_lavagem a,
	san_producao b
where	a.nr_seq_producao = b.nr_sequencia
and	a.nr_sequencia	= nr_seq_lavagem_p;

select	coalesce(max(qt_densidade_hemo),1)
into STRICT	qt_densidade_w
from	san_derivado
where	nr_sequencia = nr_seq_derivado_w;

select	round((coalesce(qt_peso_bolsa_p,0) / qt_densidade_w), 0)
into STRICT	qt_volume_w
;

return	qt_volume_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
