CREATE OR REPLACE FUNCTION san_concentracao_leucometria ( nr_sequencia_p bigint, nr_seq_producao_p bigint) RETURNS varchar AS $body$
DECLARE


vl_retorno_w		varchar(30);
ie_tipo_coleta_w	smallint;
qt_coletada_w		smallint;
qt_leucometria_c1_w	bigint;
qt_v2_w			bigint;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nr_seq_producao_p IS NOT NULL AND nr_seq_producao_p::text <> '') then

	select	max(a.ie_tipo_coleta),
		max(a.qt_coletada)
	into STRICT 	ie_tipo_coleta_w,
		qt_coletada_w
	from	san_doacao a,
		san_leucometria b
	where	a.nr_sequencia = b.nr_seq_doacao
	and	b.nr_seq_producao = nr_seq_producao_p;

	select 	max(a.qt_leucometria_c1)
	into STRICT	qt_leucometria_c1_w
	from	san_leucometria a
	where	a.nr_sequencia	= nr_sequencia_p;

	qt_v2_w	:= dividir((qt_coletada_w*qt_leucometria_c1_w),450);

	if (ie_tipo_coleta_w IS NOT NULL AND ie_tipo_coleta_w::text <> '') then
		if ( ie_tipo_coleta_w = '4') then
			vl_retorno_w := wheb_mensagem_pck.get_texto(309896); -- Diluído
		else
			if (qt_v2_w < qt_coletada_w) then
				vl_retorno_w := wheb_mensagem_pck.get_texto(309897); -- Concentrado
			else
				vl_retorno_w := wheb_mensagem_pck.get_texto(309896); -- Diluído
			end if;
		end if;
	end if;
end if;
return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
