SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION san_consiste_nomenclatura_conf ( ds_resultado_original_p text, ds_resultado_novo_p text) RETURNS varchar AS $body$
DECLARE



ds_nomenclatura_w		varchar(80);
ie_possui_nomenclatura_w	varchar(1);
ie_retorno_w			varchar(1);


BEGIN


ie_retorno_w := 'S';

if (ds_resultado_original_p IS NOT NULL AND ds_resultado_original_p::text <> '') then

	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_possui_nomenclatura_w
	from	san_exame_relacionamento
	where	ds_resultado = ds_resultado_original_p;

	if (ie_possui_nomenclatura_w = 'S') then

		select 	max(ds_nomenclatura)
		into STRICT	ds_nomenclatura_w
		from	san_exame_relacionamento
		where	ds_resultado = ds_resultado_original_p;

		if (upper(ds_nomenclatura_w) <> upper(ds_resultado_novo_p)) then
			ie_retorno_w := 'N';
		end if;
	elsif (upper(ds_resultado_original_p) <> upper(ds_resultado_novo_p)) then
		ie_retorno_w := 'N';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
