SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION san_consiste_nomenclatura ( nr_seq_exame_lote_p bigint, nr_seq_exame_p bigint) RETURNS varchar AS $body$
DECLARE



ds_nomenclatura_w		varchar(80);
qt_valor_minimo_w       	bigint;
qt_valor_maximo_w       	bigint;
ie_possui_nomenclatura_w	varchar(1);
ie_retorno_w			varchar(1);
ds_resultado_original_w		varchar(255);
ds_resultado_prim_conf_w	varchar(255);
ds_resultado_seg_conf_w		varchar(255);



BEGIN


ie_retorno_w := 'S';

if (nr_seq_exame_p IS NOT NULL AND nr_seq_exame_p::text <> '') then

	
	select 	coalesce(San_Obter_Result_Exame(nr_seq_exame_lote, nr_seq_exame), ds_resultado) ds_resultado,
		ds_resultado_prim_conf,
		ds_resultado_seg_conf
	into STRICT	ds_resultado_original_w,
		ds_resultado_prim_conf_w,
		ds_resultado_seg_conf_w
	from	san_exame_realizado
	where	nr_seq_exame_lote	= nr_seq_exame_lote_p
	and	nr_seq_exame		= nr_seq_exame_p;


	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_possui_nomenclatura_w
	from	san_exame_relacionamento
	where	trim(both upper(elimina_acentos(ds_resultado,'S'))) = trim(both upper(elimina_acentos(ds_resultado_original_w,'S')));

	if (ie_possui_nomenclatura_w = 'S') then
	
		select 	max(ds_nomenclatura),
			max(qt_valor_minimo),
			max(qt_valor_maximo)	
		into STRICT	ds_nomenclatura_w,
			qt_valor_minimo_w,
			qt_valor_maximo_w
		from	san_exame_relacionamento
		where	trim(both upper(elimina_acentos(ds_resultado,'S'))) = trim(both upper(elimina_acentos(ds_resultado_original_w,'S')));
		
		if (trim(both upper(elimina_acentos(ds_nomenclatura_w,'S'))) <> trim(both upper(elimina_acentos(ds_resultado_prim_conf_w,'S')))) or (trim(both upper(elimina_acentos(ds_nomenclatura_w,'S'))) <> trim(both upper(elimina_acentos(ds_resultado_seg_conf_w,'S')))) then
			ie_retorno_w := 'N';
		end if;
	elsif (trim(both upper(elimina_acentos(ds_resultado_original_w,'S'))) <> trim(both upper(elimina_acentos(ds_resultado_prim_conf_w,'S')))) or (trim(both upper(elimina_acentos(ds_resultado_original_w,'S'))) <> trim(both upper(elimina_acentos(ds_resultado_seg_conf_w,'S')))) then
		ie_retorno_w := 'N';	
	end if;

end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
