CREATE OR REPLACE FUNCTION san_consiste_regra_hora_coleta (nr_sequencia_p bigint, ie_tipo_coleta_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2) := 'N';
ie_sexo_pf_w		varchar(1);
hr_dif_hora_cadastro_w	integer;
hr_regra_coleta_w	integer;


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select	round((trunc((clock_timestamp() - (a.dt_triagem))*86400)/3600)),
		substr(obter_sexo_pf(a.cd_pessoa_fisica, 'C'),1,1)
	into STRICT	hr_dif_hora_cadastro_w,
		ie_sexo_pf_w
	from	san_doacao a
	where	a.nr_sequencia = nr_sequencia_p;

	select	max(x.qt_hora)
	into STRICT	hr_regra_coleta_w
	from	san_regra_retorno_coleta x
	where	x.nr_tipo_doacao = ie_tipo_coleta_p
	and	((coalesce(x.ie_sexo, 'T') = ie_sexo_pf_w) or (coalesce(x.ie_sexo, 'T') = 'T'));


	if (hr_regra_coleta_w IS NOT NULL AND hr_regra_coleta_w::text <> '') and (hr_dif_hora_cadastro_w > hr_regra_coleta_w) then
		ds_retorno_w	:= 'S';
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
