SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION san_consistir_tipagem_reversa ( nr_seq_exame_lote_p bigint, nr_seq_exame_p bigint, ds_resultado_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE



ie_tipo_exame_w	varchar(3);
ds_resultado_w 	varchar(255);
ds_retorno_w	varchar(1) := 'N';


BEGIN

if (nr_seq_exame_lote_p IS NOT NULL AND nr_seq_exame_lote_p::text <> '') and (nr_seq_exame_p IS NOT NULL AND nr_seq_exame_p::text <> '') then

	select 	coalesce(max(ie_tipo_sangue),'N')
	into STRICT	ie_tipo_exame_w
	from	san_exame
	where 	nr_sequencia = 	nr_seq_exame_p;
	
	if (ie_tipo_exame_w = 'S') then --Exame de ABO
	
		select 	max(ds_resultado)
		into STRICT	ds_resultado_w
		from	san_exame_realizado a,
			san_exame b
		where	a.nr_seq_exame		= b.nr_sequencia
		and	a.nr_seq_exame_lote 	= nr_seq_exame_lote_p
		and	coalesce(ie_tipo_exame,0)	= 3;
		
	else
		select 	coalesce(max(ie_tipo_exame),0)
		into STRICT	ie_tipo_exame_w
		from	san_exame
		where 	nr_sequencia = 	nr_seq_exame_p;
		
		if (ie_tipo_exame_w = '3') then
			select 	max(ds_resultado)
			into STRICT	ds_resultado_w
			from	san_exame_realizado a,
				san_exame b
			where	a.nr_seq_exame		= b.nr_sequencia
			and	a.nr_seq_exame_lote 	= nr_seq_exame_lote_p
			and	coalesce(ie_tipo_sangue,'N')	= 'S';
		end if;
	end if;
	
	if (ds_resultado_w IS NOT NULL AND ds_resultado_w::text <> '') and (ds_resultado_w <> ds_resultado_p) and
	 san_obter_result_exame(nr_seq_exame_lote_p, nr_seq_exame_p, ds_resultado_w) <> san_obter_result_exame(nr_seq_exame_lote_p, nr_seq_exame_p, ds_resultado_p) then
		ds_retorno_w := 'S';
	end if;

end if;	

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
